graphql.query:
query {
  listChargePaging {
    items {
      PK
      SK
      __typename     
    }
    nextToken
  }
}

query {
  getCharge(PK:"FILE#49487373", SK:"HOA#01FSMQZ6AGHCWCNT00WFR77B3R")
  {
      PK
      SK
      __typename
      ... on Charge {
        Entity {
            Nm
            Addr {
                Ln1
                Ln2
                City
                State
                County
                Zip
                
            }
        }
    }
      
  }
  
}

query {  
  getChargeDetails(SK: "HOA#01FSMQZ6AGHCWCNT00WFR77B3R")
  {
    PK
    SK
    __typename
      ... on Charge {
        Entity {
            Nm
            Addr {
                Ln1
                Ln2
                City
                State
                County
                Zip
                
            }
        }
      }
    ... on ChargeItem {
          Desc
          Cat
          Bcg
          Bcd
          Scg
          Scd
          Leu
          Chk
          PymtDtls {
            Desc
            ByrChrg
            PayTo
            PymtMthd
            TtlChrg
            LnEstRd
        }
    }
  }
}

mutation createCharge($chargeInput: ChargeInput!) {  
  createCharge(input: $chargeInput) {
    PK
    SK    
    Entity {
        Nm
        Addr {
            Ln1
            Ln2
            City
            State
            County
            Zip
            Country
        }
    }
  }
}

